mapper.select:
SELECT `e`.`order_id` , `e`.`id` , `e`.`level` , `e`.`content` , `e`.`status` , `e`.`create_time` , `e`.`creator_name` , `u`.`id` , `u`.`name` userName , `g`.`title` goodsName , `e`.`nurse_id` , `e`.`creator_id` , `u`.`phone` userPhone , `u2`.`name` nurseName FROM `evaluation` AS `e` LEFT JOIN `user` AS `u` ON (`e`.`creator_id` = `u`.`id`) LEFT JOIN `nurse` AS `n` ON (`e`.`nurse_id` = `n`.`id`) LEFT JOIN `goods` AS `g` ON (`e`.`goods_id` = `g`.`id`) LEFT JOIN `user` AS `u2` ON (`n`.`creator_id` = `u2`.`id`) WHERE 1 =1
AND `e`.`creator_name` LIKE concat(concat('%',#{userName}),'%')
AND e.`order_id` LIKE concat(concat('%',#{orderId}),'%')
AND e.`creator_id` LIKE concat(concat('%',#{creatorId}),'%')
AND e.`nurse_id` LIKE concat(concat('%',#{nurseId}),'%')
AND `e`.`create_time` >= #{beginTime}
AND `e`.`create_time` <= #{stopTime}
AND `e`.`id` = #{id}
ORDER BY e.create_time DESC

SELECT IFNULL(ROUND(AVG(
`level`)),0) level FROM `evaluation` where goods_id
=#{goodsId};
