mapper.select:
SELECT
DATE_FORMAT(o.create_time, '%Y') years, DATE_FORMAT(o.create_time, '%m') months, DATE_FORMAT(o.create_time, '%d') days, DATE_FORMAT(o.create_time, '%Y-%m-%d') daysStr, COUNT(o.create_time) num FROM `order` o WHERE (o.`schedule` BETWEEN 1 AND 5) AND o.`status` = 1 AND DATE_FORMAT(o.create_time, '%Y') =#{year}
o.device =#{device}
GROUP BY daysStr ORDER BY o.create_time

SELECT DATE_FORMAT(o.create_time, '%Y') years, DATE_FORMAT(o.create_time, '%m') months, DATE_FORMAT(o.create_time, '%d') days, DATE_FORMAT(o.create_time, '%Y-%m-%d') daysStr, COUNT(o.create_time) num FROM `order` o LEFT JOIN order_goods og ON og.order_id = o.id WHERE
(o.`schedule` BETWEEN 1 AND 5) AND o.`status` = 1 AND DATE_FORMAT(o.create_time, '%Y') =#{year}
AND og.goods_id = #{goodsId}
GROUP BY daysStr ORDER BY o.create_time

SELECT DATE_FORMAT(o.create_time, '%Y') years, DATE_FORMAT(o.create_time, '%m') months, DATE_FORMAT(o.create_time, '%d') days, DATE_FORMAT(o.create_time, '%Y-%m-%d') daysStr, COUNT(o.create_time) num FROM `order` o LEFT JOIN order_other oo ON oo.order_id = o.id WHERE (o.`schedule` BETWEEN 1 AND 5) AND o.`status` = 1 AND DATE_FORMAT(o.create_time, '%Y') =#{year}
AND (CASE WHEN LENGTH(oo.address) > 6 THEN SUBSTR(oo.address, 5, 2) ELSE oo.address END ) LIKE #{city}
GROUP BY daysStr ORDER BY o.create_time
